<?php//get the q parameter from URL$t=$_GET["which"];// Load the XML document$domDocument = new DOMDocument;$domDocument->load("detailData.xml");$domXPath = new DOMXPath($domDocument);//Substring after functionfunction after ($this, $inthat) {	if (!is_bool(strpos($inthat, $this)))    return substr($inthat, strpos($inthat,$this)+strlen($this));};//Substring before functionfunction before ($this, $inthat) {    return substr($inthat, 0, strpos($inthat, $this));};$whichSelected = after("_" , $t);$detailSelected = before("_" , $t);foreach( $domXPath->query('//detailObject') as $detailObject ) {	$name = $detailObject->getAttribute('name');	$type = $detailObject->getAttribute('type');	if($name ==  $detailSelected){		$detailContents =  $detailObject->getElementsByTagName('detailContent');		foreach ( $detailContents as $detailContent ) {			$dtNum = $detailContent->getAttribute('name');			if ($dtNum == $whichSelected) {				$imageNames = $detailContent->getElementsByTagName('detailImage');				foreach ( $imageNames as $imageName ) {					$imageNm = $imageName->textContent;				}				$detailTexts = $detailContent->getElementsByTagName('detailText');				foreach ( $detailTexts as $detailText ) {					$detailText = $detailText->textContent;				}				echo $imageNm, "_" , $detailText;			}		}	}}?>